(function(){'use strict';var module=angular.module('app.company');module.service('deleteCompanyModal',deleteCompanyModal);deleteCompanyModal.$inject=['companyResource','commonModal','notificator'];function deleteCompanyModal(companyResource,commonModal,notificator){var that=this;this.modalOptions={closeButtonText:'Cancel',actionButtonText:'Delete',headerText:'Confirm company deletion',bodyText:'The company will be deleted permanently, do you want to continue?'};this.modalDefaults={windowClass:'small-modal'};this.getDeleteMethod=function(companies){return function(o){commonModal.show(that.modalDefaults,that.modalOptions).then(function(){companyResource.delete(companies,function(){companies.$remove();notificator.success('Company was successfully deleted')});});};}}})();