(function(){'use strict';var module=angular.module('app.organization');module.service('deleteOrganizationModal',deleteOrganizationModal);deleteOrganizationModal.$inject=['organizationResource','OrganizationFactory','commonModal','notificator'];function deleteOrganizationModal(organizationResource,OrganizationFactory,commonModal,notificator){var that=this;this.modalOptions={closeButtonText:'Cancel',actionButtonText:'Delete',headerText:'Confirm organization deletion',bodyText:'The organization will be deleted permanently, do you want to continue?'};this.modalDefaults={windowClass:'small-modal'};this.getDeleteMethod=function(){OrganizationFactory.getOrganization().then(function(response){var currentOrg=response.data[0];return function(o){commonModal.show(that.modalDefaults,that.modalOptions).then(function(){organizationResource.delete(currentOrg,function(){currentOrg.$remove();notificator.success('Organization was successfully deleted')});});};})}}})();